const request = require('supertest');
const app = require('./app'); // Assuming the Express app is exported from app.js

describe('POST /create_user', () => {
  it('should create a user with a valid username', async () => {
    const res = await request(app)
      .post('/create_user')
      .send({ username: 'newUser' });
    
    expect(res.statusCode).toEqual(200);
    expect(res.body).toHaveProperty('message', 'User created successfully');
  });

  it('should not create a user with an existing username', async () => {
    const res = await request(app)
      .post('/create_user')
      .send({ username: 'existingUser' });

    expect(res.statusCode).toEqual(400);
    expect(res.body).toHaveProperty('error', 'Username already taken');
  });
});
